package com.qa.im.sqldatatypes;

/**
 * This class contains all the variables needed for the Items table fields
 * The item.id is generated by the SQL database
 * @author Admin
 *
 */
public class Item {
	private int id;
	private String name;
	private double value;

	/**
	 * constructor for making new record
	 * @param name
	 * @param value
	 */
	
	public Item(String name, double value) {
		super();
		this.name = name;
		this.value = value;
	}

	/**
	 * constructor for reading from database
	 * @param id
	 * @param name
	 * @param value
	 */
	public Item(int id, String name, double value) {
		super();
		this.id = id;
		this.name = name;
		this.value = value;
	}
	
	public Item() {
		super();
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public double getValue() {
		return value;
	}

	public void setValue(double value) {
		this.value = value;
	}

}
